SOURCES.s_f1:
            START:s_f1
///////////////////////loop gia na parei to string me ta event kai imerominies
@TELOS:INTEGER
@NAME:STRING
@TITLE:STRING
@EVE:STRING
ACTION s_DataSet2 EXECUTE  
first s_DataSet2  
LOOP   
    IF (#VALUE#<=0)
    BREAK  
    @TELOS=#s_DataSet2.RENT.ENDDATE#
    @NAME=#s_DataSet2.BOOKS.ISBN_ISSN#
    @YY:INTEGER
    @YY=(YEAROF)@TELOS
    @MM:INTEGER
    @MM=(MONTHOF)@TELOS
    @MM=@MM-1
    @DD:INTEGER
    @DD=(DAYOF)@TELOS
    @EVE=@EVE+"{title: '"+@NAME+"',start: new Date("+@YY+","+@MM+","+@DD+")},"
next s_DataSet2
ENDLOOP  
////////////////////////////////////////////////////////////////




HTML
    <script>
	    jQuery(document).ready(function() {
            jQuery('#calendar').fullCalendar({
                editable: true,
                disableDragging: true,
		   	    events:[
		   	        @EVE
		   	    ],
                eventClick: function (calEvent) {

                    callWidget(calEvent.title);
                }
		    });
	    });
    </script>

<div id='calendar'></div>




ENDHTML


RETURN 1
